package com.sweet.core.exception;import com.sweet.core.exception.enums.AbstractBaseExceptionEnum;import lombok.Data;@Datapublic class ServiceException extends RuntimeException {    private Integer code;    private String errorMessage;    public ServiceException(Integer code, String errorMessage) {        super(errorMessage);        this.code = code;        this.errorMessage = errorMessage;    }    public ServiceException(String errorMessage) {        super(errorMessage);        this.code = 500;        this.errorMessage = errorMessage;    }    public ServiceException(AbstractBaseExceptionEnum exception) {        super(exception.getMessage());        this.code = exception.getCode();        this.errorMessage = exception.getMessage();    }}