package com.sweet.core.translationDict;import java.lang.annotation.Documented;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** * 需要翻译的字典值 * * */@Target(value={ElementType.METHOD})@Retention(RetentionPolicy.RUNTIME)@Documentedpublic @interface DictParam {    /**     * 字典类型     * @return     */    String dictType() default "";    /**     * 需要翻译的字段名     * @return     */    String dictValueFiled() default "";    /**     * 被翻译的字段名     * @return     */    String dictNameFiled() default "";}