package com.sweet.modular.arranged.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.arranged.entity.Arranged;import com.sweet.modular.arranged.service.ArrangedService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 预约信息 前端控制器 * </p> * * @author admin * @since 2020-04-03 */@Controller@RequestMapping("/admin/arranged")public class ArrangedController {    @Autowired    ArrangedService arrangedService;    /**     * 列表页     */    @RequestMapping("/arranged_list")    public String list(){        return "/modular/arranged/arranged_list";    }    /**     * 编辑页     */    @RequestMapping("/arranged_edit")    public String edit(){        return "/modular/arranged/arranged_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editArranged")    @ResponseBody    public ResultBean editArranged(Arranged arranged){        arrangedService.saveOrUpdate(arranged);        return ResultBean.success(arranged);    }    /**     * 删除     */    @RequestMapping("/delArranged")    @ResponseBody    public ResultBean delArranged(Arranged arranged){        arrangedService.removeById(arranged);        return ResultBean.success(arranged);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getArrangedDetail")    @ResponseBody    public ResultBean getArrangedDetail(String id){        Arranged arranged = arrangedService.getById(id);        return ResultBean.success(arranged);    }    /**     * 列表数据     */    @RequestMapping("/getArrangedList")    @ResponseBody    @TranslationDict({    })    public LayuiPageInfo getArrangedList(Arranged Arranged){        LayuiPageInfo pageInfo = arrangedService.findPageBySpec(Arranged);        return pageInfo;    }}