package com.sweet.modular.arranged.mapper;import com.sweet.modular.arranged.entity.Arranged;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.Date;import java.util.List;/** * <p> * 预约信息 Mapper 接口 * </p> * * @author admin * @since 2020-04-03 */public interface ArrangedMapper extends BaseMapper<Arranged> {    Page<Arranged> customPageList(Page page, @Param("param") Arranged arranged);    @Select("SELECT * FROM business_arranged WHERE arranged_time>#{timeF} AND arranged_time<=#{timeR} AND patient_id=#{patient_id}")    public List<Arranged> getStime(Date timeF, Date timeR, String patient_id);}