package com.sweet.modular.arranged.service.impl;import com.sweet.modular.arranged.entity.Arranged;import com.sweet.modular.arranged.mapper.ArrangedMapper;import com.sweet.modular.arranged.service.ArrangedService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;import java.util.List;/** * <p> * 预约信息 服务实现类 * </p> * * @author admin * @since 2020-04-03 */@Servicepublic class ArrangedServiceImpl extends ServiceImpl<ArrangedMapper, Arranged> implements ArrangedService {    @Override    public LayuiPageInfo findPageBySpec(Arranged arranged) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, arranged);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public List<Arranged> getStime(String timeF, String timeR, String patient_id) throws ParseException {        SimpleDateFormat slt = new SimpleDateFormat("yyyy-MM-dd HH:mm");        Date Datef = slt.parse(timeF);        Date Dater = slt.parse(timeR);        return this.baseMapper.getStime(Datef,Dater,patient_id);    }}