package com.sweet.modular.cfnr.mapper;import com.sweet.modular.cfnr.entity.Cfnr;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Delete;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 处方药 Mapper 接口 * </p> * * @author admin * @since 2020-01-08 */public interface CfnrMapper extends BaseMapper<Cfnr> {    Page<Cfnr> customPageList(Page page, @Param("param") Cfnr cfnr);    /**     * 根据处方id查询     * @param paramCondition     * @return     */    @Select("select * from business_cfnr where recipe_id = #{recipeId}")    List<Cfnr> SearchList(String recipeId);    @Delete("delete from business_cfnr where recipe_id = #{recipeId}")    Integer delrecipeIdList(String recipeId);}