package com.sweet.modular.cfnr.service.impl;import com.sweet.modular.cfnr.entity.Cfnr;import com.sweet.modular.cfnr.mapper.CfnrMapper;import com.sweet.modular.cfnr.service.CfnrService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.util.List;/** * <p> * 处方药 服务实现类 * </p> * * @author admin * @since 2020-01-08 */@Servicepublic class CfnrServiceImpl extends ServiceImpl<CfnrMapper, Cfnr> implements CfnrService {    @Override    public LayuiPageInfo findPageBySpec(Cfnr cfnr) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, cfnr);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public List<Cfnr> findListrecipeId(String recipeId) {        List<Cfnr> list = this.baseMapper.SearchList(recipeId);        return list;    }    @Override    public void del(String recipeId) {        this.baseMapper.delrecipeIdList(recipeId);    }}