package com.sweet.modular.clinicPharmacy.service.impl;import com.sweet.modular.clinicPharmacy.entity.ClinicPharmacy;import com.sweet.modular.clinicPharmacy.mapper.ClinicPharmacyMapper;import com.sweet.modular.clinicPharmacy.service.ClinicPharmacyService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 药剂配制记录 服务实现类 * </p> * * @author admin * @since 2020-05-07 */@Servicepublic class ClinicPharmacyServiceImpl extends ServiceImpl<ClinicPharmacyMapper, ClinicPharmacy> implements ClinicPharmacyService {    @Override    public LayuiPageInfo findPageBySpec(ClinicPharmacy clinicPharmacy) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, clinicPharmacy);        return LayuiPageFactory.createPageInfo(page);    }}