package com.sweet.modular.databaseInfo.service.impl;import com.sweet.modular.databaseInfo.entity.DatabaseInfo;import com.sweet.modular.databaseInfo.mapper.DatabaseInfoMapper;import com.sweet.modular.databaseInfo.service.DatabaseInfoService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 数据库信息表 服务实现类 * </p> * * @author test * @since 2020-05-18 */@Servicepublic class DatabaseInfoServiceImpl extends ServiceImpl<DatabaseInfoMapper, DatabaseInfo> implements DatabaseInfoService {    @Override    public LayuiPageInfo findPageBySpec(DatabaseInfo databaseInfo) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, databaseInfo);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public int getsss(String id) {        return this.baseMapper.getsss(id);    }}