package com.sweet.modular.drug.mapper;import com.sweet.modular.drug.entity.Drug;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 药物表 Mapper 接口 * </p> * * @author admin * @since 2020-01-08 */public interface DrugMapper extends BaseMapper<Drug> {    Page<Drug> customPageList(Page page, @Param("param") Drug drug);    /**     *更新增加的价格     * @param param     * @return     */    @Select("update business_drug SET upprice =drug_price+drug_price*#{num} WHERE id=#{id}")    List<Drug> updateprice(@Param("param") Drug Drug, String num,String id);    @Select("select * from business_drug where id=#{id}")    public Drug getByDrugId(String id);}