package com.sweet.modular.drug.service.impl;import com.sweet.modular.drug.entity.Drug;import com.sweet.modular.drug.mapper.DrugMapper;import com.sweet.modular.drug.service.DrugService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.io.Serializable;/** * <p> * 药物表 服务实现类 * </p> * * @author admin * @since 2020-01-08 */@Servicepublic class DrugServiceImpl extends ServiceImpl<DrugMapper, Drug> implements DrugService {    @Override    public LayuiPageInfo findPageBySpec(Drug drug) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, drug);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public void updateprice(Drug drug, String num,String id) {        this.baseMapper.updateprice(drug,num,id);    }    @Override    public Drug getByDrugId(String id) {        return baseMapper.getByDrugId(id);    }}