package com.sweet.modular.patient.service.impl;import com.sweet.modular.patient.entity.Patient;import com.sweet.modular.patient.mapper.PatientMapper;import com.sweet.modular.patient.service.PatientService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.util.List;/** * <p> * 患者表 服务实现类 * </p> * * @author admin * @since 2020-01-07 */@Servicepublic class PatientServiceImpl extends ServiceImpl<PatientMapper, Patient> implements PatientService {    @Override    public LayuiPageInfo findPageBySpec(Patient patient) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, patient);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public Patient getPatientByUserId(String userId) {        return baseMapper.getPatientByUserId(userId);    }    @Override    public List<Patient> getPatientByDoctorId(String doctorId) {        return baseMapper.getPatientByDoctorId(doctorId);    }    @Override    public Patient getPatientphone(String phone_number) {        return baseMapper.getPatientphone(phone_number);    }    @Override    public Patient getPatientById(String id) {        return baseMapper.getPatientById(id);    }}