package com.sweet.modular.pictureMedical.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.pictureMedical.entity.PictureMedical;import com.sweet.modular.pictureMedical.service.PictureMedicalService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 患者拍照开药 前端控制器 * </p> * * @author admin * @since 2020-03-27 */@Controller@RequestMapping("/admin/pictureMedical")public class PictureMedicalController {    @Autowired    PictureMedicalService pictureMedicalService;    /**     * 列表页     */    @RequestMapping("/pictureMedical_list")    public String list(){        return "/modular/pictureMedical/pictureMedical_list";    }    /**     * 编辑页     */    @RequestMapping("/pictureMedical_edit")    public String edit(){        return "/modular/pictureMedical/pictureMedical_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editPictureMedical")    @ResponseBody    public ResultBean editPictureMedical(PictureMedical pictureMedical){        pictureMedicalService.saveOrUpdate(pictureMedical);        return ResultBean.success(pictureMedical);    }    /**     * 删除     */    @RequestMapping("/delPictureMedical")    @ResponseBody    public ResultBean delPictureMedical(PictureMedical pictureMedical){        pictureMedicalService.removeById(pictureMedical);        return ResultBean.success(pictureMedical);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getPictureMedicalDetail")    @ResponseBody    public ResultBean getPictureMedicalDetail(String id){        PictureMedical pictureMedical = pictureMedicalService.getById(id);        return ResultBean.success(pictureMedical);    }    /**     * 列表数据     */    @RequestMapping("/getPictureMedicalList")    @ResponseBody    @TranslationDict({    })    public LayuiPageInfo getPictureMedicalList(PictureMedical PictureMedical){        LayuiPageInfo pageInfo = pictureMedicalService.findPageBySpec(PictureMedical);        return pageInfo;    }}