package com.sweet.modular.pictureMedical.service.impl;import com.sweet.modular.pictureMedical.entity.PictureMedical;import com.sweet.modular.pictureMedical.mapper.PictureMedicalMapper;import com.sweet.modular.pictureMedical.service.PictureMedicalService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 患者拍照开药 服务实现类 * </p> * * @author admin * @since 2020-03-27 */@Servicepublic class PictureMedicalServiceImpl extends ServiceImpl<PictureMedicalMapper, PictureMedical> implements PictureMedicalService {    @Override    public LayuiPageInfo findPageBySpec(PictureMedical pictureMedical) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, pictureMedical);        return LayuiPageFactory.createPageInfo(page);    }}