package com.sweet.modular.record.mapper;import com.sweet.modular.drug.entity.Drug;import com.sweet.modular.record.entity.Record;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 患者住院档案 Mapper 接口 * </p> * * @author admin * @since 2020-01-08 */public interface RecordMapper extends BaseMapper<Record> {    Page<Record> customPageList(Page page, @Param("param") Record record);    /**     *修改就诊次数     * @param param     * @return     */    @Select("update business_record SET zycs=#{zycs} where id=#{id}")    List<Record> updatezycs(@Param("param") Record record, String id, String zycs);}