package com.sweet.modular.record.service.impl;import com.sweet.modular.record.entity.Record;import com.sweet.modular.record.mapper.RecordMapper;import com.sweet.modular.record.service.RecordService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 患者住院档案 服务实现类 * </p> * * @author admin * @since 2020-01-08 */@Servicepublic class RecordServiceImpl extends ServiceImpl<RecordMapper, Record> implements RecordService {    @Override    public LayuiPageInfo findPageBySpec(Record record) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, record);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public void updatezycs(Record record, String id, String zycs) {        this.baseMapper.updatezycs(record,id,zycs);    }}