package com.sweet.modular.reportimage.service.impl;import com.sweet.modular.reportimage.entity.Reportimage;import com.sweet.modular.reportimage.mapper.ReportimageMapper;import com.sweet.modular.reportimage.service.ReportimageService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 存放检查报告（图片） 服务实现类 * </p> * * @author admin * @since 2020-05-20 */@Servicepublic class ReportimageServiceImpl extends ServiceImpl<ReportimageMapper, Reportimage> implements ReportimageService {    @Override    public LayuiPageInfo findPageBySpec(Reportimage reportimage) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, reportimage);        return LayuiPageFactory.createPageInfo(page);    }}