package com.sweet.modular.reportresult.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.reportresult.entity.Reportresult;import com.sweet.modular.reportresult.service.ReportresultService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 存放检查结果（数据） 前端控制器 * </p> * * @author admin * @since 2020-05-20 */@Controller@RequestMapping("/admin/reportresult")public class ReportresultController {    @Autowired    ReportresultService reportresultService;    /**     * 列表页     */    @RequestMapping("/reportresult_list")    public String list(){        return "/modular/reportresult/reportresult_list";    }    /**     * 编辑页     */    @RequestMapping("/reportresult_edit")    public String edit(){        return "/modular/reportresult/reportresult_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editReportresult")    @ResponseBody    public ResultBean editReportresult(Reportresult reportresult){        reportresultService.saveOrUpdate(reportresult);        return ResultBean.success(reportresult);    }    /**     * 删除     */    @RequestMapping("/delReportresult")    @ResponseBody    public ResultBean delReportresult(Reportresult reportresult){        reportresultService.removeById(reportresult);        return ResultBean.success(reportresult);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getReportresultDetail")    @ResponseBody    public ResultBean getReportresultDetail(String id){        Reportresult reportresult = reportresultService.getById(id);        return ResultBean.success(reportresult);    }    /**     * 列表数据     */    @RequestMapping("/getReportresultList")    @ResponseBody    @TranslationDict({    })    public LayuiPageInfo getReportresultList(Reportresult Reportresult){        LayuiPageInfo pageInfo = reportresultService.findPageBySpec(Reportresult);        return pageInfo;    }}