package com.sweet.modular.reportresult.service.impl;import com.sweet.modular.reportresult.entity.Reportresult;import com.sweet.modular.reportresult.mapper.ReportresultMapper;import com.sweet.modular.reportresult.service.ReportresultService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 存放检查结果（数据） 服务实现类 * </p> * * @author admin * @since 2020-05-20 */@Servicepublic class ReportresultServiceImpl extends ServiceImpl<ReportresultMapper, Reportresult> implements ReportresultService {    @Override    public LayuiPageInfo findPageBySpec(Reportresult reportresult) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, reportresult);        return LayuiPageFactory.createPageInfo(page);    }}