package com.sweet.modular.rxdrug.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import com.sweet.core.util.RedisUtil;import org.apache.commons.lang3.StringUtils;import org.springframework.web.bind.annotation.*;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.rxdrug.entity.Rxdrug;import com.sweet.modular.rxdrug.service.RxdrugService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.stereotype.Controller;import java.util.List;import java.util.Map;/** * <p> * 处方药表 前端控制器 * </p> * * @author admin * @since 2020-01-14 */@Controller@RequestMapping("/admin/rxdrug")public class RxdrugController {    @Autowired    RxdrugService rxdrugService;    @Autowired    RedisUtil redisUtil;    /**     * 列表页     */    @RequestMapping("/rxdrug_list")    public String list(){        return "/modular/rxdrug/rxdrug_list";    }    /**     * 编辑页     */    @RequestMapping("/rxdrug_edit")    public String edit(){        return "/modular/rxdrug/rxdrug_edit";    }    /**     * 删除     */    @RequestMapping("/delRxdrug")    @ResponseBody    public ResultBean delRxdrug(Rxdrug rxdrug){        rxdrugService.removeById(rxdrug);        return ResultBean.success(rxdrug);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getRxdrugDetail")    @ResponseBody    public ResultBean getRxdrugDetail(String id){        Rxdrug rxdrug = rxdrugService.getById(id);        return ResultBean.success(rxdrug);    }    /**     * 列表数据     */    @RequestMapping("/getRxdrugList")    @ResponseBody    @TranslationDict({    })    public LayuiPageInfo getRxdrugList(Rxdrug Rxdrug){        LayuiPageInfo pageInfo = rxdrugService.findPageBySpec(Rxdrug);        return pageInfo;    }}