package com.sweet.modular.rxdrug.service.impl;import com.sweet.modular.rxdrug.entity.Rxdrug;import com.sweet.modular.rxdrug.mapper.RxdrugMapper;import com.sweet.modular.rxdrug.service.RxdrugService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import jdk.nashorn.internal.ir.LiteralNode;import org.apache.ibatis.annotations.Insert;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.util.List;/** * <p> * 处方药表 服务实现类 * </p> * * @author admin * @since 2020-01-14 */@Servicepublic class RxdrugServiceImpl extends ServiceImpl<RxdrugMapper, Rxdrug> implements RxdrugService {    @Override    public LayuiPageInfo findPageBySpec(Rxdrug rxdrug) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, rxdrug);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public void del(String recoreId) {        this.baseMapper.delList(recoreId);    }    @Override    public List<Rxdrug> slist() {        return list();    }}