package com.sweet.modular.tTemporary.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.tTemporary.entity.TTemporary;import com.sweet.modular.tTemporary.service.TTemporaryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 临时表 前端控制器 * </p> * * @author admin * @since 2020-04-22 */@Controller@RequestMapping("/admin/tTemporary")public class TTemporaryController {    @Autowired    TTemporaryService tTemporaryService;    /**     * 列表页     */    @RequestMapping("/tTemporary_list")    public String list(){        return "/modular/tTemporary/tTemporary_list";    }    /**     * 编辑页     */    @RequestMapping("/tTemporary_edit")    public String edit(){        return "/modular/tTemporary/tTemporary_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editTTemporary")    @ResponseBody    public ResultBean editTTemporary(TTemporary tTemporary){        tTemporaryService.saveOrUpdate(tTemporary);        return ResultBean.success(tTemporary);    }    /**     * 删除     */    @RequestMapping("/delTTemporary")    @ResponseBody    public ResultBean delTTemporary(TTemporary tTemporary){        tTemporaryService.removeById(tTemporary);        return ResultBean.success(tTemporary);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getTTemporaryDetail")    @ResponseBody    public ResultBean getTTemporaryDetail(String id){        TTemporary tTemporary = tTemporaryService.getById(id);        return ResultBean.success(tTemporary);    }    /**     * 列表数据     */    @RequestMapping("/getTTemporaryList")    @ResponseBody    @TranslationDict({    })    public LayuiPageInfo getTTemporaryList(TTemporary TTemporary){        LayuiPageInfo pageInfo = tTemporaryService.findPageBySpec(TTemporary);        return pageInfo;    }}