package com.sweet.modular.tTemporary.service.impl;import com.sweet.modular.tTemporary.entity.TTemporary;import com.sweet.modular.tTemporary.mapper.TTemporaryMapper;import com.sweet.modular.tTemporary.service.TTemporaryService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 临时表 服务实现类 * </p> * * @author admin * @since 2020-04-22 */@Servicepublic class TTemporaryServiceImpl extends ServiceImpl<TTemporaryMapper, TTemporary> implements TTemporaryService {    @Override    public LayuiPageInfo findPageBySpec(TTemporary tTemporary) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, tTemporary);        return LayuiPageFactory.createPageInfo(page);    }}