package com.sweet.modular.taskdetail.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.taskdetail.entity.Taskdetail;import com.sweet.modular.taskdetail.service.TaskdetailService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;import java.util.List;/** * <p> * 随访详情 前端控制器 * </p> * * @author admin * @since 2020-03-13 */@Controller    @RequestMapping("/admin/taskdetail")public class TaskdetailController {    @Autowired    TaskdetailService taskdetailService;    /**     * 列表页     */    @RequestMapping("/taskdetail_list")    public String list(){        return "/modular/taskdetail/taskdetail_list";    }    /**     * 编辑页     */    @RequestMapping("/taskdetail_edit")    public String edit(){        return "/modular/taskdetail/taskdetail_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editTaskdetail")    @ResponseBody    public ResultBean editTaskdetail(Taskdetail taskdetail){        taskdetailService.saveOrUpdate(taskdetail);        return ResultBean.success(taskdetail);    }    /**     * 删除     */    @RequestMapping("/delTaskdetail")    @ResponseBody    public ResultBean delTaskdetail(Taskdetail taskdetail){        taskdetailService.removeById(taskdetail);        return ResultBean.success(taskdetail);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getTaskdetailDetail")    @ResponseBody    public ResultBean getTaskdetailDetail(String id){        Taskdetail taskdetail = taskdetailService.getById(id);        return ResultBean.success(taskdetail);    }    /**     * 列表数据     */    @RequestMapping("/getTaskdetailList")    @ResponseBody    @TranslationDict({    })    public LayuiPageInfo getTaskdetailList(Taskdetail Taskdetail){        LayuiPageInfo pageInfo = taskdetailService.findPageBySpec(Taskdetail);        return pageInfo;    }}