package com.sweet.modular.taskdetail.mapper;import com.sweet.modular.taskdetail.entity.Taskdetail;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 随访详情 Mapper 接口 * </p> * * @author admin * @since 2020-03-13 */public interface TaskdetailMapper extends BaseMapper<Taskdetail> {    Page<Taskdetail> customPageList(Page page, @Param("param") Taskdetail taskdetail);    @Select("select * from business_taskdetail where patient_id = #{patientID} order by create_time desc")    public List<Taskdetail> getPatientTaskdetail(@Param("patientID")String patientID);}