package com.sweet.modular.template.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.template.entity.Template;import com.sweet.modular.template.service.TemplateService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 随访模板 前端控制器 * </p> * * @author admin * @since 2020-03-12 */@Controller@RequestMapping("/admin/template")public class TemplateController {    @Autowired    TemplateService templateService;    /**     * 列表页     */    @RequestMapping("/template_list")    public String list(){        return "/modular/template/template_list";    }    /**     * 编辑页     */    @RequestMapping("/template_edit")    public String edit(){        return "/modular/template/template_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editTemplate")    @ResponseBody    public ResultBean editTemplate(Template template){        templateService.saveOrUpdate(template);        return ResultBean.success(template);    }    /**     * 删除     */    @RequestMapping("/delTemplate")    @ResponseBody    public ResultBean delTemplate(Template template){        templateService.removeById(template);        return ResultBean.success(template);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getTemplateDetail")    @ResponseBody    public ResultBean getTemplateDetail(String tplId){        Template template = templateService.getById(tplId);        return ResultBean.success(template);    }    /**     * 列表数据     */    @RequestMapping("/getTemplateList")    @ResponseBody    @TranslationDict({    })    public LayuiPageInfo getTemplateList(Template Template){        LayuiPageInfo pageInfo = templateService.findPageBySpec(Template);        return pageInfo;    }}