package com.sweet.modular.template.service.impl;import com.sweet.modular.template.entity.Template;import com.sweet.modular.template.mapper.TemplateMapper;import com.sweet.modular.template.service.TemplateService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 随访模板 服务实现类 * </p> * * @author admin * @since 2020-03-12 */@Servicepublic class TemplateServiceImpl extends ServiceImpl<TemplateMapper, Template> implements TemplateService {    @Override    public LayuiPageInfo findPageBySpec(Template template) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, template);        return LayuiPageFactory.createPageInfo(page);    }}