package com.sweet.modular.testUser.service.impl;import com.sweet.modular.testUser.entity.TUser;import com.sweet.modular.testUser.mapper.TUserMapper;import com.sweet.modular.testUser.service.TUserService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> *  服务实现类 * </p> * * @author admin * @since 2020-06-14 */@Servicepublic class TUserServiceImpl extends ServiceImpl<TUserMapper, TUser> implements TUserService {    @Override    public LayuiPageInfo findPageBySpec(TUser testUser) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, testUser);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public int setIds(int id ,String name){        TUser t = new TUser();        t.setId(id);        t.setUserName(name);        return baseMapper.setIds(t);    }}