package com.sweet.modular.viewPatientinfo.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.viewPatientinfo.entity.ViewPatientinfo;import com.sweet.modular.viewPatientinfo.service.ViewPatientinfoService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 患者信息 前端控制器 * </p> * * @author admin * @since 2020-05-20 */@Controller@RequestMapping("/admin/viewPatientinfo")public class ViewPatientinfoController {    @Autowired    ViewPatientinfoService viewPatientinfoService;    /**     * 列表页     */    @RequestMapping("/viewPatientinfo_list")    public String list(){        return "/modular/viewPatientinfo/viewPatientinfo_list";    }    /**     * 编辑页     */    @RequestMapping("/viewPatientinfo_edit")    public String edit(){        return "/modular/viewPatientinfo/viewPatientinfo_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editViewPatientinfo")    @ResponseBody    public ResultBean editViewPatientinfo(ViewPatientinfo viewPatientinfo){        viewPatientinfoService.saveOrUpdate(viewPatientinfo);        return ResultBean.success(viewPatientinfo);    }    /**     * 删除     */    @RequestMapping("/delViewPatientinfo")    @ResponseBody    public ResultBean delViewPatientinfo(ViewPatientinfo viewPatientinfo){        viewPatientinfoService.removeById(viewPatientinfo);        return ResultBean.success(viewPatientinfo);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getViewPatientinfoDetail")    @ResponseBody    public ResultBean getViewPatientinfoDetail(String id){        ViewPatientinfo viewPatientinfo = viewPatientinfoService.getById(id);        return ResultBean.success(viewPatientinfo);    }    /**     * 列表数据     */    @RequestMapping("/getViewPatientinfoList")    @ResponseBody    @TranslationDict({    })    public LayuiPageInfo getViewPatientinfoList(ViewPatientinfo ViewPatientinfo){        LayuiPageInfo pageInfo = viewPatientinfoService.findPageBySpec(ViewPatientinfo);        return pageInfo;    }}