package com.sweet.modular.viewPatientinfo.service.impl;import com.sweet.modular.viewPatientinfo.entity.ViewPatientinfo;import com.sweet.modular.viewPatientinfo.mapper.ViewPatientinfoMapper;import com.sweet.modular.viewPatientinfo.service.ViewPatientinfoService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 患者信息 服务实现类 * </p> * * @author admin * @since 2020-05-20 */@Servicepublic class ViewPatientinfoServiceImpl extends ServiceImpl<ViewPatientinfoMapper, ViewPatientinfo> implements ViewPatientinfoService {    @Override    public LayuiPageInfo findPageBySpec(ViewPatientinfo viewPatientinfo) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, viewPatientinfo);        return LayuiPageFactory.createPageInfo(page);    }}