package com.sweet.modular.wxmenu.mapper;import com.sweet.modular.wxmenu.entity.Wxmenu;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Delete;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 微信菜单 Mapper 接口 * </p> * * @author admin * @since 2020-01-08 */public interface WxmenuMapper extends BaseMapper<Wxmenu> {    Page<Wxmenu> customPageList(Page page, @Param("param") Wxmenu wxmenu);    @Select("select * from business_wxmenu where 1 = 1 and (" +            "parent_id = #{appId} or parent_id in (" +            "select parent_id from business_wxmenu where parent_id " +            "like concat('%',#{appId},'%'))) order by create_time asc")    List<Wxmenu> getMenuByAppId(@Param("appId") String appId);    @Delete("delete from business_wxmenu where parent_id like concat('%',#{buttonId},'%') ")    int delButtonsById(@Param("buttonId") String buttonId);}