package com.sweet.system.controller;import com.sweet.core.model.ResultBean;import com.sweet.system.model.FileInfoResult;import com.sweet.system.service.FileInfoService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;/** * <p> * 文件信息表 前端控制器 * </p> * * @author wxl * @since 2019-12-06 */@Controller@RequestMapping("/admin/file")public class FileInfoController {    @Autowired    FileInfoService fileInfoService;    @RequestMapping("")    public String file(){        return "/system/file/file";    }    @RequestMapping("/getFileList")    @ResponseBody    public ResultBean getFileList(String parentId){       List<FileInfoResult> list =  fileInfoService.getFileList(parentId);       return ResultBean.success(200,"查询成功",list);    }}