package com.sweet.system.mapper;import com.sweet.system.model.FileInfoResult;import com.sweet.system.entity.FileInfo;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.sweet.system.service.FileInfoService;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 文件信息表 Mapper 接口 * </p> * * @author wxl * @since 2019-12-06 */public interface FileInfoMapper extends BaseMapper<FileInfo> {    @Select("select * from t_file_info where file_name=#{fileName} and file_type = '0'")    FileInfo getFileDirByName(@Param("fileName")String fileName);    @Select("select file_id id,file_name name,file_suffix type,file_path url,file_path smurl, create_time updateTime," +            "(case when file_type=0 then 'true' " +            "when file_type=1 then 'false'" +            "else null end) isDir from t_file_info where parent_id=#{parentId}" +            "order by create_time asc")    List<FileInfoResult> getFileList(@Param("parentId")String parentId);    @Select("select file_id,file_path from t_file_info where file_id in (${ids})")    List<FileInfo> getFileInfoByIds(@Param("ids") String ids);    @Select("select group_concat(file_sys_path separator '${separator}') from t_file_info where file_id in (${ids})")    String getFileSysPathByIds(@Param("ids") String ids,@Param("separator") String separator);}