package com.sweet.wxmp.util;import cn.hutool.core.date.DateUtil;import com.sweet.wxmp.WxMpProperties;import me.chanjar.weixin.common.error.WxError;import me.chanjar.weixin.common.error.WxErrorException;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.env.Environment;import org.springframework.stereotype.Component;import javax.annotation.PostConstruct;@Componentpublic class WxMpUtil {    @Autowired    WxMpProperties properties;    private static String appId ;    @PostConstruct    public void transValues() {        appId = properties.getConfigs().get(0).getAppId();    }    public static final String buildWxUrl(String url){        String CODE="https://open.weixin.qq.com/connect/oauth2/authorize?appid=APPID&redirect_uri=REDIRECT_URI&response_type=code&scope=SCOPE&state=STATE#wechat_redirect";        return CODE.replace("APPID",appId).replace("REDIRECT_URI", url).replace("SCOPE", "snsapi_userinfo").replace("STATE", "jkdn");    }}